CustomField.formula:
CASE( TEXT(Call_Frequency__c), "weekly", 
   IF(Days_since_last_activity__c > 7 && Days_since_last_activity__c < 14, "yellow", 
   IF(Days_since_last_activity__c >= 14, "red",
   "green"
   )
   )
,
"bi-weekly",
  IF(Days_since_last_activity__c > 14 && Days_since_last_activity__c < 21, "yellow", 
   IF(Days_since_last_activity__c >= 21, "red",
   "green"
   )
   )
,
"monthly",
 IF(Days_since_last_activity__c > 30 && Days_since_last_activity__c < 45, "yellow", 
   IF(Days_since_last_activity__c >=45, "red",
   "green"
   )
   )
,
"N/A"
)
